import React, { useEffect, useState } from "react";
import "./Home.css";
import { useNavigate } from "react-router-dom";


function Home() {
  const [shapes, setShapes] = useState([]);
  const [isSystemOn, setIsSystemOn] = useState(false);
  const navigate = useNavigate(); // Define navigate at the top of your component


  useEffect(() => {
    const generatedShapes = Array.from({ length: 20 }, (_, index) => ({
      id: index,
      left: Math.random() * 90, // Random horizontal position
      size: Math.random() * 30 + 20, // Random size between 20px and 50px
      color: `rgba(${Math.floor(Math.random() * 255)}, 
                    ${Math.floor(Math.random() * 255)}, 
                    ${Math.floor(Math.random() * 255)}, 0.7)`, // Random color
      shape: ["circle", "square", "triangle"][
        Math.floor(Math.random() * 3)
      ], // Random shape type
      duration: Math.random() * 5 + 5, // Random duration
      delay: Math.random() * 2, // Random delay
    }));
    setShapes(generatedShapes);
  }, []);

  const handleStartGame = () => {
    if (isSystemOn) {
      // Navigate to Game Modes only if the system is ON
      navigate("/game-modes");
      console.log("Navigating to Game Modes...");
    } else {
      // Log a warning or alert the user if the system is OFF
      console.log("Please turn on the system first!");
    }
  };

  const handleToggleSystem = () => {
    setIsSystemOn(!isSystemOn);
    console.log(`System is now ${!isSystemOn ? "ON" : "OFF"}`);
  };

  return (
    <div className="home-container">
      <div className="animated-background">
        {shapes.map((shape) => (
          <div
            key={shape.id}
            className={`shape ${shape.shape}`}
            style={{
              left: `${shape.left}%`,
              width: `${shape.size}px`,
              height: `${shape.size}px`,
              backgroundColor: shape.shape === "triangle" ? "transparent" : shape.color,
              animationDuration: `${shape.duration}s`,
              animationDelay: `${shape.delay}s`,
            }}
          >
            {shape.shape === "triangle" && (
              <div
                style={{
                  width: 0,
                  height: 0,
                  borderLeft: `${shape.size / 2}px solid transparent`,
                  borderRight: `${shape.size / 2}px solid transparent`,
                  borderBottom: `${shape.size}px solid ${shape.color}`,
                }}
              ></div>
            )}
          </div>
        ))}
      </div>
      <h1 className="title">Welcome to Montessori++</h1>
      <div className="button-group">
        <button className="home-button" onClick={handleStartGame}>
          Start Game
        </button>
        <button
          className={`home-button ${isSystemOn ? "active" : ""}`}
          onClick={handleToggleSystem}
        >
          {isSystemOn ? "Turn Off System" : "Turn On System"}
        </button>
      </div>
    </div>
  );
}

export default Home;
