import React, { useEffect, useState } from "react";
import "./GameModes.css"; // Add necessary styles

function GameModes() {
  const [shapes, setShapes] = useState([]);

  useEffect(() => {
    const generatedShapes = Array.from({ length: 20 }, (_, index) => ({
      id: index,
      left: Math.random() * 90,
      size: Math.random() * 30 + 20,
      color: `rgba(${Math.floor(Math.random() * 255)}, 
                    ${Math.floor(Math.random() * 255)}, 
                    ${Math.floor(Math.random() * 255)}, 0.7)`,
      shape: ["circle", "square", "triangle"][Math.floor(Math.random() * 3)],
      duration: Math.random() * 5 + 5,
      delay: Math.random() * 2,
    }));
    setShapes(generatedShapes);
  }, []);

  return (
    <>
      <div className="game-modes-container">
        <div className="animated-background">
          {shapes.map((shape) => (
            <div
              key={shape.id}
              className={`shape ${shape.shape}`}
              style={{
                left: `${shape.left}%`,
                width: `${shape.size}px`,
                height: `${shape.size}px`,
                backgroundColor:
                  shape.shape === "triangle" ? "transparent" : shape.color,
                animationDuration: `${shape.duration}s`,
                animationDelay: `${shape.delay}s`,
              }}
            >
              {shape.shape === "triangle" && (
                <div
                  style={{
                    width: 0,
                    height: 0,
                    borderLeft: `${shape.size / 2}px solid transparent`,
                    borderRight: `${shape.size / 2}px solid transparent`,
                    borderBottom: `${shape.size}px solid ${shape.color}`,
                  }}
                ></div>
              )}
            </div>
          ))}
        </div>
        <h1 className="title">Game Modes</h1>
        <p>Select a game mode to get started!</p>
      </div>
    </>
  );
}

export default GameModes;
