import React, { useEffect, useState } from "react";
import "./Home.css";

function Home() {
  const [shapes, setShapes] = useState([]);

  useEffect(() => {
    const generatedShapes = Array.from({ length: 30 }, (_, index) => ({
      id: index,
      left: Math.random() * 100, // Random horizontal position
      size: Math.random() * 40 + 10, // Random size between 10px and 50px
      color: `rgba(${Math.floor(Math.random() * 255)}, 
                    ${Math.floor(Math.random() * 255)}, 
                    ${Math.floor(Math.random() * 255)}, 0.7)`, // Random color
      duration: Math.random() * 5 + 5, // Random fall duration between 5s and 10s
      delay: Math.random() * 3, // Random animation delay
    }));
    setShapes(generatedShapes);
  }, []);

  const handleStartGame = () => {
    console.log("Start Game clicked!");
  };

  const handleToggleSystem = () => {
    console.log("System toggled!");
  };

  return (
    <div className="home-container">
      <div className="animated-background">
        {shapes.map((shape) => (
          <div
            key={shape.id}
            className="shape"
            style={{
              left: `${shape.left}%`,
              width: `${shape.size}px`,
              height: `${shape.size}px`,
              backgroundColor: shape.color,
              animationDuration: `${shape.duration}s`,
              animationDelay: `${shape.delay}s`,
            }}
          ></div>
        ))}
      </div>
      <h1 className="title">Welcome to Montessori App</h1>
      <div className="button-group">
        <button className="home-button" onClick={handleStartGame}>
          Start Game
        </button>
        <button className="home-button" onClick={handleToggleSystem}>
          {isSystemOn ? "Turn Off System" : "Turn On System"}
        </button>
      </div>
    </div>
  );
}

export default Home;
