import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import navigation hook
import "./Home.css"; // Ensure the correct file path

function Home() {
  const [isSystemOn, setIsSystemOn] = useState(false);
  const navigate = useNavigate(); // React Router's hook for navigation

  const handleStartGame = () => {
    navigate("/game-modes"); // Navigate to the Game Modes page
  };

  const handleToggleSystem = () => {
    setIsSystemOn(!isSystemOn);
    console.log(`System is now ${!isSystemOn ? "ON" : "OFF"}`);
  };

  return (
    <div className="home-container">
      <div className="animated-background">
        {/* Falling shapes or background design */}
      </div>
      <h1 className="title">Welcome to Montessori App</h1>
      <div className="button-group">
        <button className="home-button" onClick={handleStartGame}>
          Start Game
        </button>
        <button
          className={`home-button ${isSystemOn ? "active" : ""}`}
          onClick={handleToggleSystem}
        >
          {isSystemOn ? "Turn Off System" : "Turn On System"}
        </button>
      </div>
    </div>
  );
}

export default Home;
