import React, { useState } from "react";

function LEDControl() {
  const [ledStatus, setLedStatus] = useState(false);

  const toggleLED = () => {
    setLedStatus(!ledStatus);
    console.log(`LED is now ${!ledStatus ? "ON" : "OFF"}`);
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h1>LED Control</h1>
      <button onClick={toggleLED} style={{ margin: "10px" }}>
        {ledStatus ? "Turn Off" : "Turn On"} LED
      </button>
    </div>
  );
}

export default LEDControl;
