import React, { useEffect, useState, useContext } from 'react';
import './SystemControl.css';
import SystemContext from '../SystemContext';

function SystemControl() {
  const { isSystemOn, toggleSystem } = useContext(SystemContext);
  const [shapes, setShapes] = useState([]);
  const [deviceCount, setdeviceCount] = useState(1); // Keeping this local for now
  const [areLEDsOn, setAreLEDsOn] = useState(false); // LED master control

  // Generate falling shapes
  useEffect(() => {
    const generatedShapes = Array.from({ length: 20 }, (_, index) => ({
      id: index,
      left: Math.random() * 90,
      size: Math.random() * 30 + 20,
      color: `rgba(${Math.floor(Math.random() * 255)},
                      ${Math.floor(Math.random() * 255)},
                      ${Math.floor(Math.random() * 255)}, 0.7)`,
      shape: ['circle', 'square', 'triangle'][Math.floor(Math.random() * 3)],
      duration: Math.random() * 5 + 5,
      delay: Math.random() * 2,
    }));
    setShapes(generatedShapes);
  }, []);

  // Toggle LEDs
  const toggleLEDs = () => {
    if (!isSystemOn) {
      console.log('System must be on to control LEDs.');
      return;
    }
    setAreLEDsOn(!areLEDsOn);
    console.log(`LEDs are now ${!areLEDsOn ? 'ON' : 'OFF'}`);
  };

  return (
    <div className="system-control-container">
      {/* Falling Shapes Background */}
      <div className="animated-background">
        {shapes.map((shape) => (
          <div
            key={shape.id}
            className={`shape ${shape.shape}`}
            style={{
              left: `${shape.left}%`,
              width: `${shape.size}px`,
              height: `${shape.size}px`,
              backgroundColor:
                shape.shape === 'triangle' ? 'transparent' : shape.color,
              animationDuration: `${shape.duration}s`,
              animationDelay: `${shape.delay}s`,
            }}
          >
            {shape.shape === 'triangle' && (
              <div
                style={{
                  width: 0,
                  height: 0,
                  borderLeft: `${shape.size / 2}px solid transparent`,
                  borderRight: `${shape.size / 2}px solid transparent`,
                  borderBottom: `${shape.size}px solid ${shape.color}`,
                }}
              ></div>
            )}
          </div>
        ))}
      </div>

      {/* Dashboard */}
      <div className="dashboard">
        <h1 className="title">System Control</h1>
        <div className="status-section">
          <div className="status-item">
            <h3>System Status:</h3>
            <p
              className={`status-indicator ${
                isSystemOn ? 'connected' : 'disconnected'
              }`}
            >
              {isSystemOn ? 'Connected' : 'Disconnected'}
            </p>
            <button className="control-button" onClick={toggleSystem}>
              {isSystemOn ? 'Turn Off System' : 'Turn On System'}
            </button>
          </div>
          <div className="status-item">
            <h3>Detected Devices:</h3>
            <p className="device-count">{deviceCount}</p>
          </div>
          <div className="status-item">
            <h3>LED Master Control:</h3>
            <button
              className={`control-button ${areLEDsOn ? 'active' : ''}`}
              onClick={toggleLEDs}
              disabled={!isSystemOn} // Disable if system is off
            >
              {areLEDsOn ? 'Turn LEDs OFF' : 'Turn LEDs ON'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default SystemControl;
